mapper.select:
SELECT


,yonghu.yonghu_name as yonghuName
,yonghu.yonghu_phone as yonghuPhone
,yonghu.yonghu_id_number as yonghuIdNumber
,yonghu.yonghu_photo as yonghuPhoto
,yonghu.yonghu_email as yonghuEmail
,zhangjie.zhangjie_name as zhangjieName
,zhangjie.zhangjie_clicknum as zhangjieClicknum
,zhangjie.zhangjie_xuhao as zhangjieXuhao
,zhangjie.zhangjie_content as zhangjieContent

FROM zhangjie_collection  a
left JOIN yonghu yonghu ON a.yonghu_id = yonghu.id
left JOIN zhangjie zhangjie ON a.zhangjie_id = zhangjie.id



        and a.id in

        #{item}



        and (
            a.zhangjie_id = #{params.zhangjieId}
        )


        and (
            a.yonghu_id = #{params.yonghuId}
        )


        and a.zhangjie_collection_types = #{params.zhangjieCollectionTypes}


          and UNIX_TIMESTAMP(a.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart}) 


          and UNIX_TIMESTAMP(a.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd}) 


        and a.insert_time = #{params.insertTime}




        and a.yonghu_id IS NOT NULL


        and yonghu.yonghu_name like CONCAT('%',#{params.yonghuName},'%')


        and yonghu.yonghu_phone like CONCAT('%',#{params.yonghuPhone},'%')


        and yonghu.yonghu_id_number like CONCAT('%',#{params.yonghuIdNumber},'%')


        and yonghu.yonghu_email like CONCAT('%',#{params.yonghuEmail},'%')



        and a.zhangjie_id IS NOT NULL


        and zhangjie.shuji_id = #{params.shujiId}


        and zhangjie.zhangjie_name like CONCAT('%',#{params.zhangjieName},'%')


          and zhangjie.zhangjie_clicknum >= #{params.zhangjieClicknumStart}   


          and zhangjie.zhangjie_clicknum <= #{params.zhangjieClicknumEnd}   


        and zhangjie.zhangjie_clicknum = #{params.zhangjieClicknum}


          and zhangjie.zhangjie_xuhao >= #{params.zhangjieXuhaoStart}   


          and zhangjie.zhangjie_xuhao <= #{params.zhangjieXuhaoEnd}   


        and zhangjie.zhangjie_xuhao = #{params.zhangjieXuhao}


        and zhangjie.zhangjie_content like CONCAT('%',#{params.zhangjieContent},'%')


          and UNIX_TIMESTAMP(zhangjie.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart}) 


          and UNIX_TIMESTAMP(zhangjie.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd}) 


        and a.insert_time = #{params.insertTime}


order by a.${params.sort} ${params.order}
